import React, { Component } from "react";
import Axios from "axios";
import "./login.css";
import logo1 from "../images/logo-saensukcity.png";
import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Button,
  Card,
  NavLink,
  FormControl,
} from "react-bootstrap";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = {
      username: true,
      numberOfUsername: 13
    };
    this.handleChange = this.handleChange.bind(this);
  }
  handleChange(event) {
    const target = event.target;
    const value = target.name === 'username' ? target.checked : target.value;
    const name = target.name;

    this.setState({
      [name]: value
    });
  }

  render() {
    return (
      <Container fluid className="bg-img">

          <br/><br/><br/><br/><br/>
          <Card
            className="bg-transbox mx-auto"
            style={{ borderRadius: 30, borderColor: "#707070", minWidth: 300 }}
          >
            <br />
            <img className="img-displayed" src={logo1} />
            <Form className="mx-auto" style={{ maxWidth: 300 }}>
              <br />
              <Col>
                <FormGroup>
                  <Form.Label>ชื่อผู้ใช้งาน</Form.Label>
                  <FormControl
                    type="text"
                    maxLength="13"
                    minLength="13"
                    name="username"
                    placeholder="เลขบัตรประชาชน"
                    required
                    value={this.state.numberOfUsername}
                    onChange={this.handleChange}
                  ></FormControl>
                </FormGroup>
              </Col>
              <Col>
                <FormGroup>
                  <Form.Label>รหัสผ่าน</Form.Label>
                  <FormControl
                    type="password"
                    minLength="8"
                    name="password"
                    placeholder="รหัสผ่าน"
                    required
                  ></FormControl>
                  <NavLink className="forget-pw text-muted" href="#">
                    ลืมรหัสผ่าน?
                  </NavLink>
                  <br />
                </FormGroup>
              </Col>
              <Button
                // onClick={this.onClickValidate}
                color="primary"
                className="mx-auto d-block"
                style={{ borderRadius: 20 }}
              >
                Login
              </Button>
            </Form>
            <p style={{ textAlign: "center" }}>MyApp 2020</p>
          </Card>

      </Container>
    );
  }
}
export default Login;
