import React, { Component } from "react";
import Axios from "axios";
import "./login.css";
import logo1 from "../images/logo-saensukcity.png";
import {
  Container,
  Row,
  Col,
  Form,
  FormGroup,
  Button,
  Card,
  NavLink,
  FormControl,
} from "react-bootstrap";

class Login extends Component {
  constructor(props) {
    super(props);
    this.state = { value: '' };
    this.handleChange = this.handleChange.bind(this);
    // this.handleSubmit = this.handleSubmit.bind(this);
  }
  handleChange(event) {
    this.setState({ value: event.target.value });
  }
  validate(event) {
    var pass = event.target.value;
    var reg = '[0-9]';
    var test = reg.test(pass);
    if (test) {
      alert('pass');
    } else {
      alert('fail');
    }
  }

  // handleSubmit(event) {
  //   if (this.state.value.length < 8) {
  //     return false;
  //   }

  //   alert('A password was submitted that was ' + this.state.value.length + '    characters long.');
  //   event.preventDefault();
  // }

  render() {
    return (
      <Container fluid className="bg-img">

        <br /><br /><br /><br /><br />
        <Card
          className="bg-transbox mx-auto"
          style={{ borderRadius: 30, borderColor: "#707070", minWidth: 300 }}
        >
          <br />
          <img className="img-displayed" src={logo1} />
          <Form className="mx-auto" style={{ maxWidth: 300 }}>
            <br />
            <Col>
              <FormGroup>
                <Form.Label>ชื่อผู้ใช้งาน</Form.Label>
                <FormControl
                  type="text"
                  maxLength="13"
                  minLength="13"
                  name="username"
                  placeholder="เลขบัตรประชาชน"
                  required
                  onChange={this.handleChange(this.validate)}
                ></FormControl>
              </FormGroup>
            </Col>
            <Col>
              <FormGroup>
                <Form.Label>รหัสผ่าน</Form.Label>
                <FormControl
                  type="password"
                  minLength="8"
                  name="password"
                  placeholder="รหัสผ่าน"
                  required
                  onChange={this.handleChange}
                ></FormControl>
                <NavLink className="forget-pw text-muted" href="#">
                  ลืมรหัสผ่าน?
                  </NavLink>
                <br />
              </FormGroup>
            </Col>
            <Button
              // onClick={this.onClickValidate}
              color="primary"
              className="mx-auto d-block"
              style={{ borderRadius: 20 }}
            >
              Login
              </Button>
          </Form>
          <p style={{ textAlign: "center" }}>MyApp 2020</p>
        </Card>

      </Container>
    );
  }
}
export default Login;
